const uuid = require('uuid');
const organizationId = uuid.v4();

module.exports.config = {
    name: 'my_config',
    version: '1.0.0',
    fhirServerBaseUrl: 'https://twcore.hapi.fhir.tw/',
    action: 'return',
    fhir_version: 'R4',
    validate: false
}

